em.out.println(Arrays.toString(ogrIsmi));                ogrIsmi [2] = "ali";                ogrIsmi [3] = "zafer";                ogrIsmi [4] = "azize";                ogrIsmi [1] = "enes";                ogrIsmi [0] = "fatma";                System.out.println(Arrays.toString(ogrIsmi));                System.out.println(ogrIsmi[4]); // azize                String sehir [] = new String[5];                sehir [0] = "baybrt";                sehir [1] = "samsun";                sehir [2] = "adna";                sehir [3] = "Ankara";                sehir [4] = "denizli";                 int toplamChar = 0;                 for (int i = 0; i <sehir.length ; i++) {                    toplamChar = toplamChar + sehir[i].length();                 }                 System.out.println(toplamChar);                        // en  bast yol bu  for-each loop  (enhanced loop) dur                 int bv = 0;                 for (String r : sehir ){                     bv = bv + r.length();                 }                System.out.println(bv);    }}